mapper.update:
UPDATE MEMBER
    SET MEM_PWD = #{memPwd}
      , MEM_NAME = #{memName}
      , MEM_PHONE = #{memPhone}
      , MEM_EMAIL = #{memEmail}
      , MEM_NICKNAME = #{memNickname}
      , MEM_IMGPATH = #{memImgPath}
      , ADDRESS_CODE = #{addressCode}
      , ADDRESS = #{address}
      , ADDRESS_DETAIL = #{addressDetail}
      , ADDRESS_REF = #{addressRef}
      , MEM_MODIFYDATE = SYSDATE
WHERE MEM_ID = #{memId}

	UPDATE MEMBER
	    SET MEM_STATUS = 'N'
	      , WITHDRAW = #{withDraw}
	WHERE MEM_ID = #{memId}
  	  AND MEM_PWD = #{memPwd}
